@FixMethodOrder(MethodSorters.NAME_ASCENDING) class TestTask1FakeDrivers { private fun testFakeDrivers(fakeDriverIndexes: Set<Int>, tp: TaxiPark) { val message = "Wrong result for 'findFakeDrivers()'." + tp.display() val expected = fakeDriverIndexes.map { driver(it) }.toSet() Assert.assertEquals(message, expected, tp.findFakeDrivers()) }
