@FixMethodOrder(MethodSorters.NAME_ASCENDING) class TestTask2FaithfulPassengers { private fun testFaithfulPassengers(minTrips: Int, passengerIndexes: Set<Int>, tp: TaxiPark) { val message = "Wrong result for 'findFaithfulPassengers()'. MinTrips: $minTrips." + tp.display() val expected = passengerIndexes.map { passenger(it) }.toSet() Assert.assertEquals(message, expected, tp.findFaithfulPassengers(minTrips)) }
