package taxipark
import org.junit.Assert import org.junit.FixMethodOrder import org.junit.Test import org.junit.runners.MethodSorters
@FixMethodOrder(MethodSorters.NAME_ASCENDING) class TestTask5TheMostFrequentTripDurationPeriod {
private fun testDurationPeriod(expected: Set<IntRange?>, tp: TaxiPark) { val actual = tp.findTheMostFrequentTripDurationPeriod() val message = "Wrong result for 'findTheMostFrequentTripDurationPeriod()': $actual." if (expected.size <= 1) { Assert.assertEquals( message + tp.display(), expected.firstOrNull(), actual) } else { Assert.assertTrue(message + tp.display() + "\nPossible results: $expected" + "\nActual: $actual\n", actual?.let { it in expected } ?: expected.isEmpty()) } }
@Test fun test00() = testDurationPeriod(setOf(null), taxiPark(0..1, 0..10))
