package taxipark
import org.junit.Assert import org.junit.FixMethodOrder import org.junit.Test import org.junit.runners.MethodSorters
@FixMethodOrder(MethodSorters.NAME_ASCENDING) class TestTask6ParetoPrinciple {
private fun testPareto(expected: Boolean, tp: TaxiPark) { val message = "Wrong result for 'checkParetoPrinciple()'." + tp.display() Assert.assertEquals(message, expected, tp.checkParetoPrinciple()) }
@Test fun testParetoPrincipleSucceeds() { testPareto(true, taxiPark(1..5, 1..4, trip(1, 1, 20, 20.0), trip(1, 2, 20, 20.0), trip(1, 3, 20, 20.0), trip(1, 4, 20, 20.0), trip(2, 1, 20, 20.0))) // The income of driver #1: 160.0; // the total income of drivers #2..5: 40.0. // The first driver constitutes exactly 20% of the total of five drivers // and his relative income is 160.0 / 200.0 = 80%. }
@Test fun testParetoPrincipleFails() { testPareto(false, taxiPark(1..5, 1..4, trip(1, 1, 20, 20.0), trip(1, 2, 20, 20.0), trip(1, 3, 20, 20.0), trip(2, 4, 20, 20.0), trip(3, 1, 20, 20.0))) // The income of driver #1: 120.0; // the total income of drivers #2..5: 80.0. // The first driver constitutes 20% of the total of five drivers // but his relative income is 120.0 / 200.0 = 60% // which is less than 80%. }
@Test fun testParetoPrincipleNoTrips() = testPareto(false, taxiPark(1..5, 1..4))
@Test fun test1() = testPareto(true, taxiPark(1..5, 1..4, trip(1, 1, 20, 20.0), trip(1, 2, 20, 20.0), trip(1, 3, 20, 20.0), trip(1, 4, 20, 20.0), trip(2, 1, 20, 19.0)))
